    private void Logar(){
        try {
            
            String loginUsuario = txtLogin.getText();
            String senhaUsuario = String.valueOf(txtSenha.getPassword());

            UsuarioAutenticacaoDTO objUsuario = new UsuarioAutenticacaoDTO();
            objUsuario.setLoginUsuario(loginUsuario);
            objUsuario.setSenhaUsuario(senhaUsuario);
            
            UsuarioAutenticacaoDAO objUsuarioDAO = new UsuarioAutenticacaoDAO();
            ResultSet rsUsuarioDAO = objUsuarioDAO.autenticacaoUsuario(objUsuario);
            
            if (rsUsuarioDAO.next()) {
                // chamar tela que eu quero abrir
                 cadastroCliente cdCliente = new cadastroCliente();
                 dispose();
                 cdCliente.setVisible(true);
            }else{
                // enviar mensagem dizendo incorreto.
                JOptionPane.showMessageDialog(null, "UsuÃ¡rio ou Senha invÃ¡lida");
            }
                    
        } catch (SQLException erro) {
            JOptionPane.showMessageDialog(null, "TelaLogin" + erro);
        }

    }
